import React, { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({ nombre: "", correo: "", mensaje: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formulario enviado:", formData);
    alert("Mensaje enviado exitosamente (simulado)");
  };

  return (
    <section id="contacto" className="main-content">
      <h2 className="contact-title">Contáctanos</h2>
      <form onSubmit={handleSubmit}>
        <input name="nombre" placeholder="Nombre completo" onChange={handleChange} required />
        <input name="correo" type="email" placeholder="Correo electrónico" onChange={handleChange} required />
        <textarea name="mensaje" placeholder="Tu mensaje..." onChange={handleChange} required></textarea>
        <button type="submit" className="submit-btn">Enviar</button>
      </form>
    </section>
  );
}
