import React from "react";
import "./Planes.css";

export default function Planes() {
  const planes = [
    {
      nombre: "B√°sico",
      precio: "$29.990",
      caracteristicas: [
        "1 sitio web",
        "Soporte por correo",
        "Dominio .cl incluido",
        "Certificado SSL",
      ],
      color: "#00bcd4",
    },
    {
      nombre: "Profesional",
      precio: "$59.990",
      caracteristicas: [
        "3 sitios web",
        "Soporte prioritario 24/7",
        "Dominio + hosting incluido",
        "Optimizaci√≥n SEO avanzada",
      ],
      color: "#ff9800",
      destacado: true,
    },
    {
      nombre: "Empresarial",
      precio: "$99.990",
      caracteristicas: [
        "Sitios ilimitados",
        "Soporte VIP dedicado",
        "Infraestructura personalizada",
        "An√°lisis y reportes mensuales",
      ],
      color: "#8bc34a",
    },
  ];

  const comparativa = [
    ["Hosting incluido", "‚úÖ", "‚úÖ", "‚úÖ"],
    ["Dominio personalizado", "‚úÖ", "‚úÖ", "‚úÖ"],
    ["Optimizaci√≥n SEO", "‚ùå", "‚úÖ", "‚úÖ"],
    ["Soporte 24/7", "‚ùå", "‚úÖ", "‚úÖ"],
    ["Consultor t√©cnico dedicado", "‚ùå", "‚ùå", "‚úÖ"],
  ];

  return (
    <section className="planes-section" id="planes">
      <div className="planes-container">
        <h2 className="planes-title">Nuestros Planes</h2>
        <p className="planes-subtitle">
          Elige el plan ideal seg√∫n tus necesidades y escala con nosotros.
        </p>

        {/* üü¶ Tarjetas de planes */}
        <div className="planes-grid">
          {planes.map((plan, i) => (
            <div
              key={i}
              className={`plan-card ${plan.destacado ? "destacado" : ""}`}
              style={{
                borderColor: plan.color,
                "--card-color": plan.color,
              }}
            >
              <h3 className="plan-nombre">{plan.nombre}</h3>
              <p className="plan-precio">{plan.precio}</p>
              <ul className="plan-lista">
                {plan.caracteristicas.map((c, j) => (
                  <li key={j}>{c}</li>
                ))}
              </ul>
              <button
                className="plan-btn"
                style={{ backgroundColor: plan.color }}
              >
                Elegir Plan
              </button>
            </div>
          ))}
        </div>

        {/* üü® Tabla comparativa */}
        <div className="comparativa-container">
          <h3 className="comparativa-titulo">Comparativa de planes</h3>
          <table className="comparativa-tabla">
            <thead>
              <tr>
                <th>Caracter√≠sticas</th>
                {planes.map((p) => (
                  <th key={p.nombre}>{p.nombre}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {comparativa.map(([caract, b, p, e], i) => (
                <tr key={i}>
                  <td>{caract}</td>
                  <td>{b}</td>
                  <td>{p}</td>
                  <td>{e}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </section>
  );
}
