import React, { useEffect, useRef } from "react";
import "./Planes.css";
import planesBanner from "../assets/planes-banner.gif"; // üîπ reemplaza por tu imagen o gif real

export default function Planes() {
  const containerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => {
          if (e.isIntersecting) e.target.classList.add("visible");
        });
      },
      { threshold: 0.2 }
    );

    containerRef.current
      ?.querySelectorAll(".plan-card")
      .forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  const planes = [
    {
      nombre: "Plan B√°sico",
      precio: "$29.990 /mes",
      beneficios: [
        "Sitio web de 1 p√°gina",
        "Soporte por correo",
        "Actualizaciones mensuales",
      ],
      color: "#1e90ff",
    },
    {
      nombre: "Plan Pro",
      precio: "$59.990 /mes",
      beneficios: [
        "Hasta 5 p√°ginas web",
        "Soporte prioritario 24/7",
        "Integraci√≥n con redes y SEO b√°sico",
      ],
      color: "#00bcd4",
    },
    {
      nombre: "Plan Premium",
      precio: "$99.990 /mes",
      beneficios: [
        "Web avanzada + mantenimiento total",
        "Ciberseguridad y monitoreo",
        "Consultor√≠a personalizada",
      ],
      color: "#00e5ff",
    },
  ];

  return (
    <section className="planes-section" id="planes" ref={containerRef}>
      {/* üñºÔ∏è Encabezado */}
      <div className="planes-banner">
        <img src={planesBanner} alt="Planes Banner" />
        <div className="planes-overlay">
          <h1>Planes a tu Medida</h1>
          <p>Encuentra la soluci√≥n ideal para tu empresa o emprendimiento.</p>
        </div>
      </div>

      {/* üì¶ Contenido */}
      <div className="planes-container">
        {planes.map((plan, i) => (
          <div
            className="plan-card"
            key={i}
            style={{ "--border-color": plan.color, transitionDelay: `${i * 150}ms` }}
          >
            <h3>{plan.nombre}</h3>
            <h2>{plan.precio}</h2>
            <ul>
              {plan.beneficios.map((b, j) => (
                <li key={j}>‚úÖ {b}</li>
              ))}
            </ul>
            <button>Contratar</button>
          </div>
        ))}
      </div>
    </section>
  );
}
