// Hero.jsx
import React from "react";
import { motion } from "framer-motion";
import gifBackground from "../assets/hero.gif"; // tu gif aquí

export default function Hero() {
  return (
    <section
      id="hero"
      className="h-screen flex items-center justify-center bg-cover bg-center relative"
      style={{ backgroundImage: `url(${gifBackground})` }}
    >
      <div className="absolute inset-0 bg-black/50" />
      <motion.div
        className="relative z-10 text-center text-white p-8 rounded-2xl border border-cyan-400/40 backdrop-blur-sm"
        animate={{ boxShadow: ["0 0 20px #00ffff33", "0 0 40px #00ffff55"] }}
        transition={{ duration: 2, repeat: Infinity, repeatType: "mirror" }}
      >
        <h1 className="text-5xl font-bold mb-4">
          Soluciones en Redes e Informática
        </h1>
        <p className="text-lg text-gray-200">
          Diseño Web  • Soporte Técnico • Soluciones Computacionales
        </p>
      </motion.div>
    </section>
  );
}
