import React from "react";
import "./Hero.css";

export default function Hero() {
  const panels = [
    { id: 1, title: "Innovación", color: "#06b6e6" },
    { id: 2, title: "Soporte TI", color: "#0284c7" },
    { id: 3, title: "Desarrollo", color: "#0ea5e9" },
    { id: 4, title: "Consultoría", color: "#38bdf8" },
    { id: 5, title: "Infraestructura", color: "#7dd3fc" },
  ];

  return (
    <section id="hero" className="hero-section">
      <div className="hero-container">
        {panels.map((panel) => (
          <div
            key={panel.id}
            className="hero-panel"
            style={{
            backgroundImage: `url(/assets/infra.jpg)`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            }}
          >
            <h2>{panel.title}</h2>
          </div>
        ))}
      </div>
    </section>
  );
}
