import React, { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({
    nombre: "",
    correo: "",
    categoria: "",
    mensaje: "",
  });

  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch("http://localhost:4000/api/contact", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setSuccess(true);
        setFormData({ nombre: "", correo: "", categoria: "", mensaje: "" });
      } else {
        setSuccess(false);
      }
    } catch (error) {
      console.error("Error enviando formulario:", error);
      setSuccess(false);
    }

    setLoading(false);
  };

  return (
    <section
      id="contacto"
      className="bg-gradient-to-b from-sky-900 to-sky-950 text-white py-20 px-6 font-mullish"
    >
      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-10 items-start">
        {/* üßæ FORMULARIO */}
        <form
          onSubmit={handleSubmit}
          className="bg-white/10 backdrop-blur-md p-8 rounded-2xl shadow-xl border border-white/20"
        >
          <h1 className="text-4xl font-bold text-sky-300 mb-8 text-center">
            Cont√°ctanos
          </h1>

          <div className="flex flex-col gap-5">
            {/* Nombre */}
            <div>
              <label className="block mb-1 text-sky-200">Nombre completo</label>
              <input
                type="text"
                name="nombre"
                placeholder="Tu nombre completo"
                value={formData.nombre}
                onChange={handleChange}
                required
                className="w-full px-4 py-2 rounded-lg bg-sky-950/30 border border-sky-800 text-white placeholder-sky-400 focus:outline-none focus:ring-2 focus:ring-sky-400"
              />
            </div>

            {/* Correo */}
            <div>
              <label className="block mb-1 text-sky-200">
                Correo electr√≥nico
              </label>
              <input
                type="email"
                name="correo"
                placeholder="tuemail@ejemplo.com"
                value={formData.correo}
                onChange={handleChange}
                required
                className="w-full px-4 py-2 rounded-lg bg-sky-950/30 border border-sky-800 text-white placeholder-sky-400 focus:outline-none focus:ring-2 focus:ring-sky-400"
              />
            </div>

            {/* Categor√≠a */}
            <div>
              <label className="block mb-1 text-sky-200">
                Categor√≠a de consulta
              </label>
              <select
                name="categoria"
                value={formData.categoria}
                onChange={handleChange}
                required
                className="w-full px-4 py-2 rounded-lg bg-sky-950/30 border border-sky-800 text-white focus:outline-none focus:ring-2 focus:ring-sky-400"
              >
                <option value="">Selecciona una categor√≠a...</option>
                <option>Atenci√≥n de Emergencias. SOS Soporte.cl</option>
                <option>Instalaci√≥n y revisi√≥n de equipos</option>
                <option>Mantenci√≥n de red y equipos computacionales</option>
                <option>Asesor√≠a TI Storage y respaldos</option>
                <option>Manutenciones L√≥gicas y F√≠sicas</option>
                <option>Cableado estructurado</option>
                <option>Asesor√≠a TI Servidores Web</option>
                <option>Configuraci√≥n de Servidores y correos</option>
                <option>Configuraci√≥n de Impresoras de red</option>
                <option>Dise√±o Web a medida</option>
                <option>Plataformas de sistemas web</option>
                <option>Migraci√≥n de correos</option>
              </select>
            </div>

            {/* Mensaje */}
            <div>
              <label className="block mb-1 text-sky-200">Mensaje</label>
              <textarea
                name="mensaje"
                placeholder="Escribe tu mensaje aqu√≠..."
                value={formData.mensaje}
                onChange={handleChange}
                required
                className="w-full px-4 py-3 rounded-lg bg-sky-950/30 border border-sky-800 text-white placeholder-sky-400 focus:outline-none focus:ring-2 focus:ring-sky-400 h-32 resize-none"
              ></textarea>
            </div>

            {/* Bot√≥n */}
            <button
              type="submit"
              disabled={loading}
              className="w-full mt-4 bg-sky-600 hover:bg-sky-500 transition-all py-3 rounded-lg font-semibold text-white shadow-md disabled:opacity-70"
            >
              {loading ? "Enviando..." : "Enviar"}
            </button>

            {success === true && (
              <p className="text-green-400 mt-2 text-center">
                ‚úÖ Mensaje enviado con √©xito
              </p>
            )}
            {success === false && (
              <p className="text-red-400 mt-2 text-center">
                ‚ùå Error al enviar el mensaje
              </p>
            )}
          </div>
        </form>

        {/* üìç INFO */}
        <div className="space-y-6 text-sky-100">
          <div>
            <h2 className="text-2xl font-semibold mb-3 text-sky-300">
              Nuestra ubicaci√≥n
            </h2>
            <p>üìç Santiago, Chile</p>
          </div>

          <div className="rounded-2xl overflow-hidden shadow-lg border border-sky-800">
            <iframe
              title="Ubicaci√≥n RIDS"
              src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3329.9325850449927!2d-70.6150744!3d-33.4250019!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x9662cf640dc37b43%3A0xf7b5d4def64fe908!2sLa%20Concepci%C3%B3n%2065%2C%20oficina%201003%2C%20Providencia%2C%20Santiago%2C%20Chile!5e0!3m2!1ses!2scl!4v1762364010014!5m2!1ses!2scl"
              width="100%"
              height="250"
              style={{ border: 0 }}
              allowFullScreen
              loading="lazy"
              referrerPolicy="no-referrer-when-downgrade"
            ></iframe>
          </div>

          <div>
            <h2 className="text-2xl font-semibold mb-2 text-sky-300">
              Cont√°ctanos
            </h2>
            <p>üìû +56 9 7371 3869</p>
            <p>üìû +56 9 8807 6593</p>
            <div className="mt-3 space-y-1">
              <a
                href="mailto:soporte@rids.cl"
                className="text-sky-400 hover:underline"
              >
                soporte@rids.cl
              </a>
              <br />
              <a
                href="mailto:carenas@rids.cl"
                className="text-sky-400 hover:underline"
              >
                carenas@rids.cl
              </a>
              <br />
              <a
                href="mailto:informaciones@rids.cl"
                className="text-sky-400 hover:underline"
              >
                informaciones@rids.cl
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
