import React, { useEffect, useRef } from "react";
import "./Servicios1.css";
import serviciosImg from "../assets/servicios-banner.gif"; // ‚ö†Ô∏è pon aqu√≠ tu imagen o GIF real

export default function Servicios1() {
  const containerRef = useRef(null);

  // üü¶ Observer para animaciones de entrada
  useEffect(() => {
    const el = containerRef.current;
    if (!el) return;
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => {
          if (e.isIntersecting) e.target.classList.add("visible");
        });
      },
      { threshold: 0.2 }
    );

    el.querySelectorAll(".s1-card").forEach((card) => observer.observe(card));
    return () => observer.disconnect();
  }, []);

  const servicios = [
    {
      icon: "üíª",
      titulo: "Desarrollo Web",
      descripcion:
        "Creamos sitios modernos, r√°pidos y personalizados, con las √∫ltimas tecnolog√≠as y un dise√±o profesional.",
    },
    {
      icon: "üõ†Ô∏è",
      titulo: "Soporte y Mantenimiento",
      descripcion:
        "Brindamos soporte t√©cnico continuo, mantenimiento preventivo y correctivo 24/7 para tu empresa.",
    },
    {
      icon: "üåê",
      titulo: "Redes e Infraestructura",
      descripcion:
        "Dise√±amos e implementamos redes seguras, eficientes y adaptadas a tu entorno corporativo.",
    },
    {
      icon: "üé®",
      titulo: "Dise√±o UX/UI",
      descripcion:
        "Dise√±os centrados en el usuario, combinando est√©tica e intuitividad para maximizar resultados.",
    },
    {
      icon: "‚òÅÔ∏è",
      titulo: "Servicios en la Nube",
      descripcion:
        "Migramos tus sistemas a la nube con soluciones seguras, flexibles y escalables.",
    },
    {
      icon: "üîí",
      titulo: "Ciberseguridad",
      descripcion:
        "Protegemos tus activos digitales con auditor√≠as, monitoreo y respuesta ante incidentes.",
    },
  ];

  return (
    <section className="s1-section" id="servicios1" ref={containerRef}>
      {/* üñºÔ∏è Encabezado con imagen de fondo o GIF */}
      <div className="s1-banner">
        <img src={serviciosImg} alt="Banner Servicios" />
        <div className="s1-banner-overlay">
          <h1>Nuestros Servicios</h1>
          <p>Innovaci√≥n, tecnolog√≠a y seguridad al servicio de tu empresa</p>
        </div>
      </div>

      {/* üü¶ Contenido principal */}
      <div className="s1-inner max-w-6xl mx-auto px-6">
        <h2 className="s1-title">Soluciones Integrales en Tecnolog√≠a</h2>
        <p className="s1-sub">
          Impulsamos tu negocio con herramientas digitales dise√±adas para crecer contigo.
        </p>

        <div className="s1-grid">
          {servicios.map((s, i) => (
            <article
              className="s1-card"
              key={i}
              style={{ transitionDelay: `${i * 90}ms` }}
            >
              <div className="s1-icon">{s.icon}</div>
              <h3 className="s1-card-title">{s.titulo}</h3>
              <p className="s1-card-desc">{s.descripcion}</p>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
}
