import React, { useEffect, useRef, useState } from "react";
import Slider from "react-slick"; // üåÄ Carrusel
import "./Servicios1.css";

import img1 from "../assets/hero1.png";
import img2 from "../assets/hero2.png";
import img3 from "../assets/hero3.png";
import img4 from "../assets/hero4.png";
import img5 from "../assets/hero5.png";

export default function Servicios1() {
  const [activeCard, setActiveCard] = useState(null);

  // üé† Configuraci√≥n del carrusel
  const settings = {
    dots: false,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 2000, // üîπ velocidad m√°s r√°pida
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    pauseOnHover: false,
  };

  const servicios = [
    {
      icon: "üíª",
      titulo: "Desarrollo Web",
      descripcion:
        "Creamos sitios web r√°pidos, modernos y escalables con las mejores tecnolog√≠as actuales.",
    },
    {
      icon: "üé®",
      titulo: "Dise√±o UX/UI",
      descripcion:
        "Creamos interfaces atractivas, intuitivas y centradas en la experiencia del usuario.",
    },
    {
      icon: "üîí",
      titulo: "Ciberseguridad",
      descripcion:
        "Protegemos tus sistemas frente a amenazas con soluciones de seguridad avanzadas.",
    },
    {
      icon: "‚òÅÔ∏è",
      titulo: "Servicios en la Nube",
      descripcion:
        "Migramos tus sistemas a la nube con alta disponibilidad y flexibilidad.",
    },
    {
      icon: "üõ†Ô∏è",
      titulo: "Soporte y Mantenimiento",
      descripcion:
        "Monitoreo, mantenimiento y asistencia t√©cnica personalizada para tu empresa.",
    },
  ];

  return (
    <section className="servicios-section" id="servicios">
      {/* üé† Carrusel de im√°genes */}
      <div className="servicios-carousel">
        <Slider {...settings}>
          {[img1, img2, img3, img4].map((img, i) => (
            <div key={i}>
              <img src={img} alt={`Slide ${i}`} className="servicios-img" />
              <div className="servicios-overlay">
                <h1 className="servicios-title">Nuestros Servicios</h1>
                <p className="servicios-sub">
                  Innovaci√≥n, tecnolog√≠a y seguridad al servicio de tu empresa
                </p>
              </div>
            </div>
          ))}
        </Slider>
      </div>

      {/* üß© Cards */}
      <div className="servicios-container">
        <h2 className="servicios-heading">Soluciones Tecnol√≥gicas</h2>
        <p className="servicios-text">
          Adaptamos nuestras soluciones a las necesidades reales de tu negocio.
        </p>

        <div className="servicios-grid">
          {servicios.map((s, i) => (
            <div
              key={i}
              className={`servicios-card ${activeCard === i ? "active" : ""}`}
              onClick={() => setActiveCard(activeCard === i ? null : i)}
            >
              <div className="servicios-icon">{s.icon}</div>
              <h3 className="servicios-card-title">{s.titulo}</h3>
              <p className="servicios-card-desc">{s.descripcion}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
