import React from "react";
import { motion } from "framer-motion";
import gifBackground from "../assets/hero.gif";
import "./Hero.css";

export default function Hero() {
  return (
    <section
      id="hero"
      style={{ backgroundImage: `url(${gifBackground})` }}
    >
      {/* Luces decorativas */}
      <div className="hero-light cyan"></div>
      <div className="hero-light pink"></div>

      {/* Contenido principal */}
      <motion.div
        className="hero-content"
        animate={{ boxShadow: ["0 0 25px #00ffff33", "0 0 50px #00ffff77"] }}
        transition={{ duration: 2, repeat: Infinity, repeatType: "mirror" }}
      >
        <h1>Soluciones en Redes e Informática</h1>
        <p>Diseño Web • Soporte Técnico • Soluciones Computacionales</p>
      </motion.div>
    </section>
  );
}
