import React, { useEffect, useRef } from "react";
import "./Servicios1.css";

export default function Servicios1() {
  const containerRef = useRef(null);

  // peque√±o observer para animar las tarjetas al entrar en pantalla
  useEffect(() => {
    const el = containerRef.current;
    if (!el) return;
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => {
          if (e.isIntersecting) {
            e.target.classList.add("visible");
          }
        });
      },
      { threshold: 0.2 }
    );

    el.querySelectorAll(".s1-card").forEach((card) => observer.observe(card));
    return () => observer.disconnect();
  }, []);

  const servicios = [
    {
      icon: "üíª",
      titulo: "Desarrollo Web",
      descripcion:
        "Sitios y plataformas web modernas, r√°pidos, y optimizados para tu negocio.",
    },
    {
      icon: "üõ†Ô∏è",
      titulo: "Soporte y Mantenimiento",
      descripcion:
        "Soporte t√©cnico, mantenimiento preventivo y resoluci√≥n de incidencias 24/7.",
    },
    {
      icon: "üåê",
      titulo: "Redes e Infraestructura",
      descripcion:
        "Dise√±o e implementaci√≥n de redes, cableado estructurado y seguridad de red.",
    },
    {
      icon: "üé®",
      titulo: "Dise√±o UX/UI",
      descripcion:
        "Interfaz enfocada en la experiencia de usuario para convertir visitantes en clientes.",
    },
    {
      icon: "‚òÅÔ∏è",
      titulo: "Servicios en la Nube",
      descripcion:
        "Migraci√≥n y administraci√≥n en la nube: backups, servidores y disponibilidad.",
    },
    {
      icon: "üîí",
      titulo: "Ciberseguridad",
      descripcion:
        "Evaluaciones de vulnerabilidad, protecci√≥n y monitoreo continuo.",
    },
  ];

  return (
    <section className="s1-section" id="servicios1" ref={containerRef}>
      <div className="s1-inner max-w-6xl mx-auto px-6">
        <h2 className="s1-title">Nuestros Servicios</h2>
        <p className="s1-sub">
          Soluciones integrales en TI ‚Äî creaci√≥n, protecci√≥n y mantenimiento.
        </p>

        <div className="s1-grid">
          {servicios.map((s, i) => (
            <article
              className="s1-card"
              key={i}
              style={{ transitionDelay: `${i * 90}ms` }}
            >
              <div className="s1-icon">{s.icon}</div>
              <h3 className="s1-card-title">{s.titulo}</h3>
              <p className="s1-card-desc">{s.descripcion}</p>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
}
