import React from "react";
import { motion } from "framer-motion";
import gifBackground from "../assets/hero.gif"; // tu gif aquí

export default function Hero() {
  return (
    <section
      id="hero"
      className="relative flex items-center justify-center h-screen bg-cover bg-center overflow-hidden"
      style={{ backgroundImage: `url(${gifBackground})` }}
    >
      {/* Capa oscura para contraste */}
      <div className="absolute inset-0 bg-black/60" />

      {/* Brillos flotantes decorativos */}
      <div className="absolute inset-0 pointer-events-none overflow-hidden">
        <div className="absolute top-10 left-1/4 w-64 h-64 bg-cyan-400/20 rounded-full blur-3xl animate-float-slow" />
        <div className="absolute bottom-10 right-1/4 w-72 h-72 bg-pink-500/20 rounded-full blur-3xl animate-float-slower" />
      </div>

      {/* Contenido principal */}
      <motion.div
        className="relative z-10 text-center text-white p-8 rounded-2xl border border-cyan-400/40 backdrop-blur-md shadow-lg"
        initial={{ opacity: 0, y: 30 }}
        animate={{
          opacity: 1,
          y: 0,
          boxShadow: ["0 0 20px #00ffff33", "0 0 40px #00ffff55"],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "mirror",
          ease: "easeInOut",
        }}
      >
        <motion.h1
          className="text-4xl md:text-6xl font-bold mb-4 tracking-tight"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 1 }}
        >
          Soluciones en{" "}
          <span className="text-cyan-300">Redes e Informática</span>
        </motion.h1>

        <motion.p
          className="text-lg md:text-xl text-gray-200"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 1 }}
        >
          Diseño Web • Soporte Técnico • Soluciones Computacionales
        </motion.p>

        <motion.div
          className="mt-8 flex justify-center gap-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1, duration: 1 }}
        >
          <button className="px-6 py-3 bg-cyan-400 text-black font-semibold rounded-full hover:bg-cyan-300 transition transform hover:scale-105">
            Contáctanos
          </button>
          <button className="px-6 py-3 border border-cyan-300 text-cyan-300 rounded-full hover:bg-cyan-300 hover:text-black transition transform hover:scale-105">
            Ver Servicios
          </button>
        </motion.div>
      </motion.div>
    </section>
  );
}
