import React, { useEffect, useRef, useState } from "react";
import Slider from "react-slick"; // üåÄ Carrusel
import "./Servicios1.css";

import img1 from "../assets/hero1.png";
import img2 from "../assets/hero2.png";
import img3 from "../assets/hero3.png";
import img4 from "../assets/hero4.png";
import img5 from "../assets/hero5.png";

export default function Servicios1() {
  const containerRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(null);

  // üîπ Animaciones de aparici√≥n
  useEffect(() => {
    const el = containerRef.current;
    if (!el) return;
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => {
          if (e.isIntersecting) e.target.classList.add("visible");
        });
      },
      { threshold: 0.2 }
    );
    el.querySelectorAll(".s1-card").forEach((card) => observer.observe(card));
    return () => observer.disconnect();
  }, []);

  // üß† Datos de servicios
  const servicios = [
    {
      icon: "üíª",
      titulo: "Desarrollo Web",
      descripcion:
        "Creamos sitios modernos, r√°pidos y personalizados con tecnolog√≠as de √∫ltima generaci√≥n.",
    },
    {
      icon: "üõ†Ô∏è",
      titulo: "Soporte y Mantenimiento",
      descripcion:
        "Brindamos soporte continuo, mantenimiento preventivo y correctivo para tu infraestructura digital.",
    },
    {
      icon: "üåê",
      titulo: "Redes e Infraestructura",
      descripcion:
        "Dise√±amos redes seguras, escalables y adaptadas a las necesidades de tu organizaci√≥n.",
    },
    {
      icon: "üé®",
      titulo: "Dise√±o UX/UI",
      descripcion:
        "Dise√±os centrados en la experiencia del usuario para maximizar impacto y usabilidad.",
    },
    {
      icon: "‚òÅÔ∏è",
      titulo: "Servicios en la Nube",
      descripcion:
        "Migramos tus sistemas a la nube con soluciones escalables y seguras.",
    },
    {
      icon: "üîí",
      titulo: "Ciberseguridad",
      descripcion:
        "Protegemos tus activos digitales con auditor√≠as y estrategias de defensa avanzadas.",
    },
  ];

  // üé† Configuraci√≥n del carrusel
  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    fade: true,
  };

  return (
    <section className="s1-section" ref={containerRef} id="servicios1">
      {/* üé† Carrusel superior */}
      <div className="s1-carousel">
        <Slider {...settings}>
          {[img1, img2, img3, img4, img5].map((img, i) => (
            <div key={i}>
              <img src={img} alt={`Servicio ${i + 1}`} className="s1-carousel-img" />
              <div className="s1-carousel-overlay">
                <h1 className="s1-carousel-title">Nuestros Servicios</h1>
                <p className="s1-carousel-sub">
                  Innovaci√≥n, tecnolog√≠a y seguridad al servicio de tu empresa
                </p>
              </div>
            </div>
          ))}
        </Slider>
      </div>

      {/* üì¶ Contenido de servicios */}
      <div className="s1-inner">
        <h2 className="s1-title">Soluciones Integrales en Tecnolog√≠a</h2>
        <p className="s1-sub">
          Impulsamos tu negocio con herramientas digitales dise√±adas para crecer contigo.
        </p>

        <div className="s1-grid">
          {servicios.map((s, i) => (
            <article
              className={`s1-card ${activeIndex === i ? "active" : ""}`}
              key={i}
              style={{ transitionDelay: `${i * 100}ms` }}
              onClick={() => setActiveIndex(activeIndex === i ? null : i)}
            >
              <div className="s1-icon">{s.icon}</div>
              <h3 className="s1-card-title">{s.titulo}</h3>
              <p className="s1-card-desc">{s.descripcion}</p>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
}
