import React, { useEffect, useState } from "react";
import { Link } from "react-scroll";
import "./Header.css";
import LogoRids from "../assets/LOGO_RIDS_blanco.png"; // ✅ Ruta corregida

export default function Header() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 20);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`header ${scrolled ? "scrolled" : ""}`}>
      <div className="logo-container">
        <img
  src={LogoRids}
  alt="Logo RIDS"
  className="logo-img"
  style={{ height: "55px", width: "auto" }}
/>
      </div>

      <nav className="nav-links">
        <Link to="hero" smooth={true} duration={700} offset={-70}>
          Inicio
        </Link>
        <Link to="about" smooth={true} duration={700} offset={-70}>
          Sobre Nosotros
        </Link>
        <Link to="services" smooth={true} duration={700} offset={-70}>
          Servicios
        </Link>
        <Link to="contact" smooth={true} duration={700} offset={-70}>
          Contacto
        </Link>
      </nav>
    </header>
  );
}
