import React from "react";
import { motion } from "framer-motion";
import "./Hero.css"; // Importar el CSS
import gif1 from "../assets/hero.gif";


export default function Hero() {
  return (
    <section className="hero-section">
      {/* Carrusel de GIFs */}
      <div className="hero-bg">
        <img src={gif1} alt="Fondo 1" />
        <img src={gif2} alt="Fondo 2" />
        <img src={gif1} alt="Fondo 3" /> {/* Repite para loop suave */}
      </div>

      {/* Capa oscura */}
      <div className="hero-overlay"></div>

      {/* Luces decorativas */}
      <div className="floating-light light-cyan"></div>
      <div className="floating-light light-pink"></div>

      {/* Contenido principal */}
      <motion.div
        className="hero-content"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <motion.h1
          className="text-4xl md:text-6xl font-bold mb-4"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 1 }}
        >
          Soluciones en{" "}
          <span className="text-cyan-300">Redes e Informática</span>
        </motion.h1>

        <motion.p
          className="text-lg md:text-xl text-gray-200"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 1 }}
        >
          Diseño Web • Soporte Técnico • Soluciones Computacionales
        </motion.p>

        <motion.div
          className="mt-8 flex justify-center gap-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1, duration: 1 }}
        >
          <button className="px-6 py-3 bg-cyan-400 text-black font-semibold rounded-full hover:bg-cyan-300 transition transform hover:scale-105">
            Contáctanos
          </button>
          <button className="px-6 py-3 border border-cyan-300 text-cyan-300 rounded-full hover:bg-cyan-300 hover:text-black transition transform hover:scale-105">
            Ver Servicios
          </button>
        </motion.div>
      </motion.div>
    </section>
  );
}
