// Header.jsx
import React from "react";
import { Link as ScrollLink } from "react-scroll";
import { Link } from "react-router-dom";

export default function Header() {
  return (
    <header className="bg-white/10 backdrop-blur-lg shadow-md fixed top-0 w-full z-50 border-b border-blue-400/30">
      <nav className="container mx-auto flex justify-between items-center py-4 px-6">
        <h1 className="text-2xl font-bold text-cyan-400">RIDS</h1>
        <ul className="flex space-x-6 text-white">
          <li>
            <ScrollLink
              to="hero"
              smooth={true}
              duration={700}
              className="cursor-pointer hover:text-cyan-300 transition"
            >
              Inicio
            </ScrollLink>
          </li>
          <li>
            <Link to="/sobrenosotros" className="hover:text-cyan-300 transition">
              Sobre Nosotros
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}
