import React from "react";
import "./Hero.css";

// Importa las imágenes
import img1 from "../assets/hero1.jpg";
import img2 from "../assets/hero2.jpg";
import img3 from "../assets/hero3.jpg";
import img4 from "../assets/hero4.jpg";
import img5 from "../assets/hero5.jpg";

export default function Hero() {
  const panels = [
    { id: 1, title: "Innovación", image: img1 },
    { id: 2, title: "Soporte TI", image: img2 },
    { id: 3, title: "Desarrollo", image: img3 },
    { id: 4, title: "Consultoría", image: img4 },
    { id: 5, title: "Infraestructura", image: img5 },
  ];

  return (
    <section id="hero" className="hero-section">
      <div className="hero-container">
        {panels.map((panel, index) => (
          <div
            key={panel.id}
            className="hero-panel"
            style={{
              backgroundImage: `url(${panel.image})`,
              animationDelay: `${index * 0.15}s`, // retrasa la aparición secuencial
            }}
          >
            <div className="overlay"></div>
            <h2>{panel.title}</h2>
          </div>
        ))}
      </div>
    </section>
  );
}
